import React, { useEffect, useState } from 'react'
import { BrowserRouter, Link, NavLink, Route, Switch } from 'react-router-dom'
import Home from './pages'
import about from './pages/about'
import '../App.css';
import baru from './pages/baru';


function Effect() {
  const [count, setCount] = useState(0)
  useEffect(() => {
    document.title = `document title ${count}`
    return () => { document.title = "React App" }
  }, [count])
  return (
    <div>
      <BrowserRouter>
        <Switch>
          <Route component={Home} path="/" exact />
          <Route component={about} path="/about" />
          <Route component={baru} path="/baru" />


        </Switch>
        <div>
          <Link to="/" /* className="hai" */ style={{ margin: "50px" }} >Home</Link>
          <Link to="/about" style={{ marginRight: "50px" }}>About</Link>
          {/* pakai activeStyle untuk memberikan style pada link, bisanya pada navlink */}
          <NavLink to="/baru" activeStyle={{ backgroundColor: "red", color: "white" }}>Baru</NavLink>
        </div>
      </BrowserRouter>
      <p>You Clicked <strong>{count}</strong></p>
      <button onClick={() => setCount((p) => p + 1)}> Click for plus</button>
      <button onClick={() => setCount(count - 1)}> Click for min</button>

    </div >
  )
}

export default Effect
