import React, { useState } from "react";
import { useForm } from "react-hook-form";

export default function Form() {
  const {
    register,
    handleSubmit,
    reset,
    getValues,
    formState: { errors },
  } = useForm();
  const [data,setData]=useState()
  const onSubmit =  (data) => {
    console.log(data);
    setData(getValues())
    reset()

  };

  return (
    <div>
      <h1>Input Data Tim Sepakbola</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        <label>Nama Tim </label>

        <input type="text" {...register("tim", { required: true })} />
        {/* required true, artinya dibutuhkan bener2 harus diisi */}
        {/* validasi berbentuk object seperti required dan lain lain ya adick adick */}

        {errors.tim && <p>Harus kamu isi ya adick-adick</p>}

        <label>Logo Tim</label>

        <input type="file" {...register("logo")} />

        <label>Tahun Berdiri</label>

        <input type="number" {...register("tahun", { min: 1999, max: 2025 })} />

        {errors.tahun && (
          <p className="error">Minimal tahun 1999 dan maximal tahun 2025</p>
        )}

        <label>Alamat Markas Tim</label>

        <input type="text" {...register("alamat")} />

        <label>Kota Markas Tim</label>

        <input type="text" {...register("kota", { pattern: /^[A-Za-z]+$/i })} />

        {errors.kota && <p>Isi dengan alphabet aja jangan kasih nomor</p>}

        <button type="submit"  >Submit</button>

      </form>
      <div>
       <h3> {data && data.tim}</h3>
       <h3> {data && data.logo}</h3>
       <h3> {data && data.tahun}</h3>
       <h3> {data && data.alamat}</h3>
       <h3> {data && data.kota}</h3>


      </div>
      {/* object tidak bisa ditampilkan langsung maka harus distringkan dulu */}

    </div>
  );
}
